import React, { useState } from 'react';
import './dashboard.css';

const Dashboard = (props) => {
    const [tokenAddress, setTokenAddress] = useState('');
    const [tokenAddressVerified, toggleTokenAddressVerified] = useState(false);
    const [transferRecipient, setTransferRecipient] = useState('');
    const [transferAmount, setTransferAmount] = useState('');

    return (
        <div>
            {tokenAddressVerified ?
                <form>
                    <div className="form-row"><label>Token Address: {tokenAddress}</label></div>
                    <h3>Transfer Tokens</h3>
                    <div className="form-row">
                        <label>Recipient</label>
                        <input value={transferRecipient} onChange={(event)=>setTransferRecipient(event.target.value)}/>
                        <label>Amount</label>
                        <input value={transferAmount} onChange={(event)=>setTransferAmount(event.target.value)}/>
                        <input type="button"/></div>
                    <div className="form-row btn-row"><input type="button" value="Change Token" className="btn" onClick={()=>{
                        setTokenAddress('');
                        toggleTokenAddressVerified(false);
                    }}/></div>

                </form>
                :
                <form>
                    <div className="form-row"><label>Token Address</label><input type="text" value={tokenAddress} onChange={(event)=>setTokenAddress(event.target.value)}/></div>
                    <div className="form-row btn-row"><input type="button" value="Get Token" className="btn" onClick={()=>toggleTokenAddressVerified(true)}/></div>
                </form>
            }
        </div>
    );
}
export default Dashboard;
