import logo from './logo.svg';
import './App.css';
import BuildForm from './Components/BuildForm.jsx';
import ConnectButton from './Components/ConnectButton.jsx'
import { useState, useEffect } from 'react';
import { ethers } from 'ethers';

import CustomERC20Builder from './artifacts/contracts/CustomERC20Builder.sol/CustomERC20Builder.json';
const contractAddress = '';

function App() {
  const [connected, setConnected] = useState(window.ethereum.eth_accounts.length > 0);


  const connect = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum, "any");
    console.log(await provider.send("eth_requestAccounts", []));
    const signer = provider.getSigner();
    const contract = new ethers.Contract(contractAddress, CustomERC20Builder.abi, signer);
  }

  useEffect(() => {
    window.ethereum.on('connected', () => {
      setConnected(window.ethereum.eth_accounts.length > 0);
    });
    window.ethereum.on('accountsChanged', (accounts) => {
      setConnected(accounts.length > 0);
      console.log(window.ethereum.isConnected());
    });
  });

  return (
    <div className="App">
      <h1>Token Builder</h1>
      <h2>Build your own ERC20 token on the Ethereum blockchain!</h2>
      {connected ? <BuildForm/> : <ConnectButton onConnect={connect}/>}
    </div>
  );
}

export default App;
