import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const TokenEntry = () => {
    const handleSubmit = async (event) => {
        event.preventDefault();
        const startTime = Date.now();
        await verifyTokenAddress(tokenAddress).then(() => {
            console.log("Ran in " + (Date.now() - startTime) + "ms");
        });
    }

    const verifyTokenAddress = async (addr) => {
        try {
            const provider = new ethers.providers.Web3Provider(window.ethereum, "any");
            await provider.send("eth_requestAccounts", []);
            const signer = provider.getSigner();

            const erc20 = new ethers.Contract(addr, abi, signer);
            const pUser = signer.getAddress();
            const pName = erc20.name();
            const pSymbol = erc20.symbol();
            const pDecimals = erc20.decimals();
            const [user, name, symbol, decimals] = await Promise.all([pUser, pName, pSymbol, pDecimals]);
            const balance = await erc20.balanceOf(user);
            setTokenData({
                tokenAddress: addr,
                accountAddress: user,
                balance: balance.toString(),
                name: name,
                symbol: symbol,
                decimals: decimals
            });
            toggleTokenAddressVerified(true);
        } catch (e) { 
            console.error(e);
            alert('Error: Token address invalid.');
        }
    }

    return (
        <Form onSubmit={handleSubmit}>
            <div className="form-row"><label>Token Address</label><input type="text" value={tokenAddress} className="text-input" onChange={(event)=>setTokenAddress(event.target.value)}/></div>
            <div className="form-row btn-row"><Button variant="primary" onClick={handleSubmit}>Get Token</Button></div>
        </Form>
    )
}

export default TokenEntry;
